mapper.update:
update t_admin_log 
	set update_Date = now()
		,log_type = #{logType}
		,operator = #{operator}
		,request_ip = #{requestIp}
		,request_method = #{requestMethod}
		,request_params = #{requestParams}
		,method_describle = #{methodDescrible}
		,exception_code = #{exceptionCode}
		,exception_detail = #{exceptionDetail}
		,create_date = #{createDate}
		,update_date = #{updateDate}
where log_id = #{logId}

mapper.select:
select 
		log_id
		,log_type
		,operator
		,request_ip
		,request_method
		,request_params
		,method_describle
		,exception_code
		,exception_detail
		,create_date
		,update_date
 from
t_admin_log


	log_type = #{logType}


	and operator = #{operator}


	and request_ip = #{requestIp}


	and request_method = #{requestMethod}


	and request_params = #{requestParams}


	and method_describle = #{methodDescrible}


	and exception_code = #{exceptionCode}


	and exception_detail = #{exceptionDetail}


	and create_date = #{createDate}


	and update_date = #{updateDate}


order by create_date desc limit 1;

