mapper.update:
update t_admin_menu 
	set update_Date = now()
		,menu_name = #{menuName}
		,menu_url = #{menuUrl}
		,valid_flag = #{validFlag}
		,sort_id = #{sortId}
		,menu_type = #{menuType}
		,create_date = #{createDate}
		,update_date = #{updateDate}
		,parent_id = #{parentId}
		,icon = #{icon}
		,search_flag = #{searchFlag}
		,delete_flag = #{deleteFlag}
		,update_flag = #{updateFlag}
		,add_flag = #{addFlag}
		,batch_delete_flag = #{batchDeleteFlag}
		,update_date = now()
where menu_id = #{menuId}

