mapper.update:
update t_admin_role 
	set update_Date = now()
		,role_name = #{roleName}
		,description = #{description}
		,menu_id = #{menuId}
		,valid_flag = #{validFlag}
		,sort_id = #{sortId}
		,update_date = now()
where role_id = #{roleId}

mapper.select:
select 
		role_id
		,role_name
		,description
		,valid_flag
		,sort_id
		,create_date
		,update_date
 from
t_admin_role


	role_name = #{roleName}


	and description = #{description}


	and valid_flag = #{validFlag}


	and sort_id = #{sortId}


	and create_date = #{createDate}


	and update_date = #{updateDate}


order by sort_id ,create_date desc limit 1;

